-- Assumes bWorlds are same size
-- Left-biased such that the background character and any singular points they share are taken from the left
combineTwoAsciiWorlds :: AsciiWorld -> AsciiWorld -> AsciiWorld
combineTwoAsciiWorlds w1 w2
    = w1 { bWorldMasks = M.unionWith combineMasks (bWorldMasks w1) (bWorldMasks w2),
           bWorldPoints = M.unionWith combinePoints (bWorldPoints w1) (bWorldPoints w2) }
  where combineMasks :: Mask -> Mask -> Mask
        combineMasks = bitwiseOr
        
        combinePoints :: Point -> Point -> Point
        combinePoints point1 _ = point1

combineAsciiWorlds :: [AsciiWorld] -> AsciiWorld
combineAsciiWorlds = foldr1 combineTwoAsciiWorlds

isNamedPoint :: String -> Point -> AsciiWorld -> Bool
isNamedPoint name point bWorld = inPoints
  where
    inPoints = case M.lookup name (bWorldPoints bWorld) of
        Just p -> p == point
        Nothing -> False

isInNamedMask :: String -> Point -> AsciiWorld -> Bool
isInNamedMask name point bWorld = inMasks
  where
    inMasks = case M.lookup name (bWorldMasks bWorld) of
        Just bits -> testBit bits (pointToIndex (bWorldWidth bWorld) point)
        Nothing -> False

isNamedPointOrInNamedMask :: String -> Point -> AsciiWorld -> Bool
isNamedPointOrInNamedMask name point bWorld = inPoints || inMasks
  where
    inPoints = isNamedPoint name point bWorld
    inMasks = isInNamedMask name point bWorld

moveNamedMask :: String -> (Int,Int) -> AsciiWorld -> AsciiWorld
moveNamedMask name (dx,dy) w = w {bWorldMasks = M.update (\pts -> Just $ moveMask width (dx,dy) pts) name (bWorldMasks w)}
  where width = bWorldWidth w

moveNamedPoint :: String -> (Int,Int) -> AsciiWorld -> AsciiWorld
moveNamedPoint name (dx,dy) w = w {bWorldPoints = M.update (\pt -> Just $ movePoint width (dx,dy) pt) name (bWorldPoints w)}
  where width = bWorldWidth w

copyNamedMask :: String -> String -> AsciiWorld -> AsciiWorld
copyNamedMask srcName destName w = fromMaybe w $ do
    mask <- M.lookup srcName (bWorldMasks w)
    return $ w {bWorldMasks = M.insert destName mask (bWorldMasks w)}

applyNamedMask :: (Mask -> Mask -> Mask) -> String -> String -> AsciiWorld -> AsciiWorld
applyNamedMask op modifier target w
    | not (modifier `M.member` bWorldMasks w) = error $ "applyNamedMask: not (modifier `M.member` bWorldMasks w) == False where modifier == " ++ modifier
    | not (target   `M.member` bWorldMasks w) = error $ "applyNamedMask: not (target `M.member` bWorldMasks w) == False where target == " ++ modifier
    | otherwise = w {bWorldMasks = M.insert target newMask (bWorldMasks w)}
  where mask1 = fromJust $ M.lookup modifier (bWorldMasks w)
        mask2 = fromJust $ M.lookup target   (bWorldMasks w)
        newMask = mask1 `op` mask2

