setPoint :: String -> (Int,Int) -> AsciiWorld -> AsciiWorld
setPoint name (x,y) w = w {bWorldPoints = M.insert name (x,y) (bWorldPoints w)}

insertMaskAtPoint :: String -> String -> AsciiWorld -> Maybe AsciiWorld
insertMaskAtPoint layerName pointName w = do
    point <- M.lookup pointName (bWorldPoints w)
    let newMask = pointToMask width point
    return $ w {bWorldMasks = M.insert layerName newMask (bWorldMasks w)}
  where width = bWorldWidth w

isOverlappingMasks :: String -> String -> AsciiWorld -> Bool
isOverlappingMasks name1 name2 w
    = fromMaybe False $ do
        points1 <- M.lookup name1 (bWorldMasks w)
        points2 <- M.lookup name2 (bWorldMasks w)
        
        return $ points1 `isOverlapping` points2


-- Make partitioner which gives every disconnected part of a layer it's own name by tacking on the next number not already existing in the bit mask map
